import React, { useEffect, useState } from 'react';
import API from '../services/api';
import { LogOut, Calendar, ClipboardList } from 'lucide-react';

export default function EmployeeDashboard() {
  const [balance, setBalance] = useState(0);
  const [leaves, setLeaves] = useState([]);
  const [start, setStart] = useState('');
  const [end, setEnd] = useState('');
  const [reason, setReason] = useState('');
  const [msg, setMsg] = useState('');

  const fetch = async () => {
    try {
      const b = await API.get('/leave/balance');
      setBalance(b.data.leaveBalance);
      const L = await API.get('/leave/my');
      setLeaves(L.data);
    } catch (e) {
      console.error(e);
    }
  };

  useEffect(() => { fetch(); }, []);

  const apply = async (e) => {
    e.preventDefault();
    setMsg('');
    try {
      await API.post('/leave/apply', { startDate: start, endDate: end, reason });
      setMsg('✅ Leave applied successfully!');
      setStart(''); setEnd(''); setReason('');
      fetch();
    } catch (err) {
      setMsg(err.response?.data?.msg || '❌ Error applying leave');
    }
  };

  const logout = () => { localStorage.clear(); window.location.href = '/login'; };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      {/* Header */}
      <header className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800">Employee Dashboard</h2>
        <button 
          onClick={logout} 
          className="flex items-center gap-2 bg-red-500 text-white px-4 py-2 rounded-xl shadow hover:bg-red-600 transition"
        >
          <LogOut size={18} /> Logout
        </button>
      </header>

      {/* Leave Balance */}
      <section className="mb-6">
        <div className="bg-white p-6 rounded-2xl shadow-md flex items-center gap-4">
          <Calendar className="text-blue-600" size={40} />
          <div>
            <p className="text-gray-600">Available Leave Balance</p>
            <h3 className="text-3xl font-bold text-gray-800">{balance}</h3>
          </div>
        </div>
      </section>

      {/* Apply Leave Form */}
      <section className="mb-6">
        <div className="bg-white p-6 rounded-2xl shadow-md">
          <h3 className="text-xl font-semibold mb-4 text-gray-700">Apply for Leave</h3>
          <form onSubmit={apply} className="grid md:grid-cols-2 gap-4">
            <input 
              type="date" 
              value={start} 
              onChange={e=>setStart(e.target.value)} 
              required
              className="border rounded-xl p-3 focus:ring-2 focus:ring-blue-400 outline-none"
            />
            <input 
              type="date" 
              value={end} 
              onChange={e=>setEnd(e.target.value)} 
              required
              className="border rounded-xl p-3 focus:ring-2 focus:ring-blue-400 outline-none"
            />
            <input 
              value={reason} 
              onChange={e=>setReason(e.target.value)} 
              placeholder="Reason" 
              className="md:col-span-2 border rounded-xl p-3 focus:ring-2 focus:ring-blue-400 outline-none"
            />
            <button 
              type="submit" 
              className="md:col-span-2 bg-blue-600 text-white py-3 rounded-xl shadow hover:bg-blue-700 transition"
            >
              Apply Leave
            </button>
          </form>
          {msg && <div className="mt-4 text-center text-sm font-medium text-green-600">{msg}</div>}
        </div>
      </section>

      {/* My Leaves Table */}
      <section>
        <div className="bg-white p-6 rounded-2xl shadow-md">
          <h3 className="text-xl font-semibold mb-4 text-gray-700 flex items-center gap-2">
            <ClipboardList className="text-green-600" /> My Leaves
          </h3>
          <div className="overflow-x-auto">
            <table className="w-full border-collapse">
              <thead>
                <tr className="bg-gray-100 text-gray-600">
                  <th className="p-3 text-left">From</th>
                  <th className="p-3 text-left">To</th>
                  <th className="p-3 text-center">Days</th>
                  <th className="p-3 text-center">Status</th>
                </tr>
              </thead>
              <tbody>
                {leaves.length === 0 ? (
                  <tr>
                    <td colSpan="4" className="p-4 text-center text-gray-500">No leaves applied yet</td>
                  </tr>
                ) : (
                  leaves.map(l => (
                    <tr key={l._id} className="border-t hover:bg-gray-50">
                      <td className="p-3">{new Date(l.startDate).toLocaleDateString()}</td>
                      <td className="p-3">{new Date(l.endDate).toLocaleDateString()}</td>
                      <td className="p-3 text-center">{l.days}</td>
                      <td className={`p-3 text-center font-medium ${
                        l.status === 'Approved' ? 'text-green-600' : 
                        l.status === 'Pending' ? 'text-yellow-600' : 'text-red-600'
                      }`}>
                        {l.status}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </section>
    </div>
  );
}
