import React from "react";
import { useNavigate, useParams } from "react-router-dom";

export default function AuthChoice() {
  const { role } = useParams(); // "employee" or "admin"
  const nav = useNavigate();

  return (
    <div className="center">
      <h2>{role === "admin" ? "Admin" : "Employee"} Access</h2>
      <button onClick={() => nav(`/login/${role}`)}>Login</button>
      <button onClick={() => nav(`/register/${role}`)}>Register</button>
    </div>
  );
}
