import React, { useEffect, useState } from "react";
import API from "../services/api";

export default function AdminDashboard() {
  const [employees, setEmployees] = useState([]);
  const [requests, setRequests] = useState([]);
  const [form, setForm] = useState({ name: "", email: "", password: "", department: "", joiningDate: "" });

  useEffect(() => {
    const fetchData = async () => {
      const emps = await API.get("/employee/list");
      setEmployees(emps.data);
      const reqs = await API.get("/leave/all"); // assumed endpoint
      setRequests(reqs.data);
    };
    fetchData();
  }, []);

  const addEmployee = async (e) => {
    e.preventDefault();
    await API.post("/employee/add", form);
    alert("Employee Added!");
    window.location.reload();
  };

  const updateStatus = async (id, status) => {
    await API.put(`/leave/${id}/${status}`);
    window.location.reload();
  };

  return (
    <div className="center">
      <h1 className="title">🧑‍💼 Admin Dashboard</h1>

      <div className="card">
        <h2 className="subtitle">Add New Employee</h2>
        <form onSubmit={addEmployee} className="form-grid">
          <input placeholder="Name" value={form.name} onChange={e=>setForm({...form,name:e.target.value})} required />
          <input type="email" placeholder="Email" value={form.email} onChange={e=>setForm({...form,email:e.target.value})} required />
          <input type="password" placeholder="Password" value={form.password} onChange={e=>setForm({...form,password:e.target.value})} required />
          <input placeholder="Department" value={form.department} onChange={e=>setForm({...form,department:e.target.value})} />
          <input type="date" placeholder="Joining Date" value={form.joiningDate} onChange={e=>setForm({...form,joiningDate:e.target.value})} />
          <button className="btn secondary" type="submit">Add Employee</button>
        </form>
      </div>

      <div className="card">
        <h2 className="subtitle">Pending Leave Requests</h2>
        <table className="styled-table">
          <thead>
            <tr>
              <th>Employee</th><th>From</th><th>To</th><th>Reason</th><th>Action</th>
            </tr>
          </thead>
          <tbody>
            {requests.filter(r=>r.status==="pending").map((r,i)=>(
              <tr key={i}>
                <td>{r.employeeId?.name}</td>
                <td>{new Date(r.startDate).toLocaleDateString()}</td>
                <td>{new Date(r.endDate).toLocaleDateString()}</td>
                <td>{r.reason}</td>
                <td>
                  <button className="btn primary" onClick={()=>updateStatus(r._id,"approve")}>Approve</button>
                  <button className="btn danger" onClick={()=>updateStatus(r._id,"reject")}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="card">
        <h2 className="subtitle">All Employees</h2>
        <table className="styled-table">
          <thead>
            <tr><th>Name</th><th>Email</th><th>Dept</th><th>Balance</th></tr>
          </thead>
          <tbody>
            {employees.map((e,i)=>(
              <tr key={i}>
                <td>{e.name}</td><td>{e.email}</td><td>{e.department}</td><td>{e.leaveBalance}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
