import React, { useEffect, useState } from 'react';
import API from '../services/api';

export default function AdminDashboard() {
  const [employees, setEmployees] = useState([]);
  const [pending, setPending] = useState([]);
  const [form, setForm] = useState({ name:'', email:'', department:'', joiningDate:'' });
  const [msg, setMsg] = useState('');

  const fetch = async () => {
    try {
      const e = await API.get('/admin/employees');
      setEmployees(e.data);
      const p = await API.get('/admin/leaves/pending');
      setPending(p.data);
    } catch (err) {
      console.error(err);
    }
  };
  useEffect(()=>{ fetch(); }, []);

  const add = async (ev) => {
    ev.preventDefault(); setMsg('');
    try { await API.post('/admin/employee/add', form); setMsg('Added'); setForm({ name:'', email:'', department:'', joiningDate:''}); fetch(); }
    catch(e){ setMsg(e.response?.data?.msg || 'Error'); }
  };

  const act = async (id, type) => {
    try{
      if(type==='approve') await API.put(`/admin/leave/${id}/approve`);
      else await API.put(`/admin/leave/${id}/reject`);
      fetch();
    }catch(e){ console.error(e); }
  };

  const logout = () => { localStorage.clear(); window.location.href = '/login'; };

  return (
    <div className="container">
      <header>
        <h2>Admin Dashboard</h2>
        <button onClick={logout}>Logout</button>
      </header>
      <section className="card">
        <h3>Add Employee</h3>
        <form onSubmit={add}>
          <input required placeholder="Name" value={form.name} onChange={e=>setForm({...form,name:e.target.value})} />
          <input required placeholder="Email" value={form.email} onChange={e=>setForm({...form,email:e.target.value})} />
          <input placeholder="Department" value={form.department} onChange={e=>setForm({...form,department:e.target.value})} />
          <input type="date" required value={form.joiningDate} onChange={e=>setForm({...form,joiningDate:e.target.value})} />
          <button type="submit">Add</button>
        </form>
        {msg && <div className="msg">{msg}</div>}
      </section>

      <section className="card">
        <h3>Pending Leaves</h3>
        <table>
          <thead><tr><th>Employee</th><th>From</th><th>To</th><th>Days</th><th>Actions</th></tr></thead>
          <tbody>
            {pending.map(p=> (
              <tr key={p._id}>
                <td>{p.employeeId?.name}</td>
                <td>{new Date(p.startDate).toLocaleDateString()}</td>
                <td>{new Date(p.endDate).toLocaleDateString()}</td>
                <td>{p.days}</td>
                <td>
                  <button onClick={()=>act(p._id,'approve')}>Approve</button>
                  <button onClick={()=>act(p._id,'reject')}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      <section className="card">
        <h3>Employees</h3>
        <table>
          <thead><tr><th>Name</th><th>Email</th><th>Dept</th><th>Joining</th><th>Balance</th></tr></thead>
          <tbody>
            {employees.map(emp=> (
              <tr key={emp._id}><td>{emp.name}</td><td>{emp.email}</td><td>{emp.department}</td><td>{new Date(emp.joiningDate).toLocaleDateString()}</td><td>{emp.leaveBalance}</td></tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
}
