import React, { useState, useEffect } from "react";
import API from "../services/api";

export default function EmployeeDashboard() {
  const [balance, setBalance] = useState(0);
  const [leaves, setLeaves] = useState([]);
  const [form, setForm] = useState({ startDate: "", endDate: "", reason: "" });

  useEffect(() => {
    const fetchData = async () => {
      const bal = await API.get("/leave/balance");
      setBalance(bal.data.balance);
      const hist = await API.get("/leave/my");
      setLeaves(hist.data);
    };
    fetchData();
  }, []);

  const applyLeave = async (e) => {
    e.preventDefault();
    await API.post("/leave/apply", form);
    alert("Leave applied!");
    window.location.reload();
  };

  return (
    <div className="center">
      <h1 className="title">üë®‚Äçüíº Employee Dashboard</h1>
      <div className="card">
        <h2 className="subtitle">Leave Balance</h2>
        <p className="big-text">{balance} Days Remaining</p>
      </div>

      <div className="card">
        <h2 className="subtitle">Apply for Leave</h2>
        <form onSubmit={applyLeave} className="form-grid">
          <input type="date" value={form.startDate} onChange={e => setForm({...form, startDate: e.target.value})} required />
          <input type="date" value={form.endDate} onChange={e => setForm({...form, endDate: e.target.value})} required />
          <input placeholder="Reason" value={form.reason} onChange={e => setForm({...form, reason: e.target.value})} required />
          <button className="btn primary" type="submit">Submit</button>
        </form>
      </div>

      <div className="card">
        <h2 className="subtitle">My Leave History</h2>
        <table className="styled-table">
          <thead>
            <tr>
              <th>From</th><th>To</th><th>Reason</th><th>Status</th>
            </tr>
          </thead>
          <tbody>
            {leaves.map((l, i) => (
              <tr key={i}>
                <td>{new Date(l.startDate).toLocaleDateString()}</td>
                <td>{new Date(l.endDate).toLocaleDateString()}</td>
                <td>{l.reason}</td>
                <td><span className={`status ${l.status}`}>{l.status}</span></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
